import React from 'react'
// import Operations from '../Operations'

class Numbers extends React.Component {
    constructor(){
        super()
        this.state = {
            input: "",
            addition: "",
            equal: ""
        }
        this.handleClick = this.handleClick.bind(this)
        this.handleAdd = this.handleAdd.bind(this)
        this.handleEqual = this.handleEqual.bind(this)
    }
    handleClick(event) {
        console.log(event.target.value)
        this.setState({
            input: event.target.value 
        })
    }
    handleAdd(event) {
        console.log(event.target.value)
        this.setState({addition: event.target.value})
    }
    handleEqual(event) {
        console.log(event.target.value + event.target.value)
        this.setState({equal: event.target.value})
    }
    render() {
        return (
            <div className="row m-3">
                <div>
                    <h1>{this.state.input}</h1>
                </div>
                <div className="col-sm">
                    <button onClick={this.handleClick} value="1" className="btn btn-dark m-1 btn-lg">1</button>
                    <button onClick={this.handleClick} value="2" className="btn btn-dark m-1 btn-lg">2</button>
                    <button onClick={this.handleClick} value="3" className="btn btn-dark m-1 btn-lg">3</button>
                    <br />
                    <button onClick={this.handleClick} value="4" className="btn btn-dark m-1 btn-lg">4</button>
                    <button onClick={this.handleClick} value="5" className="btn btn-dark m-1 btn-lg">5</button>
                    <button onClick={this.handleClick} value="6" className="btn btn-dark m-1 btn-lg">6</button>
                    <br />
                    <button onClick={this.handleClick} value="7" className="btn btn-dark m-1 btn-lg">7</button>
                    <button onClick={this.handleClick} value="8" className="btn btn-dark m-1 btn-lg">8</button>
                    <button onClick={this.handleClick} value="9" className="btn btn-dark m-1 btn-lg">9</button>
                    <br />
                    <button onClick={this.handleClick} value="0" className="btn btn-dark m-1 btn-lg">0</button>
                </div>
                {/* <Operations /> */}
                <div>
                <button onClick={this.handleAdd}>+</button>
                <button onClick={this.handleEqual}>=</button>
                <div>
                    <h1>{this.state.equal}</h1>
                </div>
                </div>
            </div>
        )
    }
}
export default Numbers
