    render() {

        return (
            <div>
                <button onClick={this.handleNum1Prop} value="1" className="btn btn-dark m-1 btn-lg">1</button>
                <button onClick={this.handleNum1Prop} value="2" className="btn btn-dark m-1 btn-lg">2</button>
                <button onClick={this.handleNum1Prop} value="3" className="btn btn-dark m-1 btn-lg">3</button>
                <br />
                <button onClick={this.handleNum1Prop} value="4" className="btn btn-dark m-1 btn-lg">4</button>
                <button onClick={this.handleNum1Prop} value="5" className="btn btn-dark m-1 btn-lg">5</button>
                <button onClick={this.handleNum1Prop} value="6" className="btn btn-dark m-1 btn-lg">6</button>
                <br />
                <button onClick={this.handleNum1Prop} value="7" className="btn btn-dark m-1 btn-lg">7</button>
                <button onClick={this.handleNum1Prop} value="8" className="btn btn-dark m-1 btn-lg">8</button>
                <button onClick={this.handleNum1Prop} value="9" className="btn btn-dark m-1 btn-lg">9</button>
                <br />
                <button onClick={this.handleNum1Prop} value="0" className="btn btn-dark m-1 btn-lg">0</button>
                <button onClick={this.handleNum1Prop} value="." className="btn btn-dark m-1 btn-lg">.</button>
            </div>
        )
    }
}
export default FirstInput
