import React from 'react';
import FirstInput from '../FirstInput'

class Calculator extends React.Component {
    constructor(){
        super()
        this.state = {
            inputField: "",
            result: ""
        }
        this.clear = this.clear.bind(this) 
        this.handleChange = this.handleChange.bind(this)
    }
    clear() {
        window.location.reload(false)
    }
    handleChange(event) {
        this.setState({
            inputField: [...this.state.inputField, event.target.value].join('')
        })
    }    
    convertPercentage(event) {
        const{inputField} = this.state
        if (event.target.value.includes('%')){
           this.setState({inputField: [...this.state.inputField, parseFloat(event.target.value) / 100] })  
        }
    } 
    getResult = () => {
        const{inputField} = this.state
        this.setState({
            result: eval(inputField)
        })
    }

    render() {
        return (
            <div className="row m-3">
                <div className="navbar-toggler font-weight-bold">
                    <h1 className="border border-info" 
                        onChange={this.handleChange}>
                            {this.state.inputField}
                        </h1>
                    <h1  className="border border-info">{this.state.result}</h1>
                    <br />
                    <FirstInput 
                        clear={this.clear} 
                        handleinputFieldProps={this.handleChange} 
                        convertPercentage={this.convertPercentage} 
                        result={this.getResult} 
                    />
                </div>
            </div>
        )
    }
}

export default Calculator
