import React from 'react'

class Calculator extends React.Component {
    constructor(){
        super()
        this.state = {
            formSpace: "",
            add: "",
            subtract: "",
            total: ""
        }
        this.handleOneClick = this.handleOneClick.bind(this)
        this.handleAddClick = this.handleAddClick.bind(this)
        this.handleSubractClick = this.handleSubractClick.bind(this)
        this.handleTotalClick = this.handleTotalClick.bind(this)
    }

    handleAddClick() {
        console.log("adding")
    }
    handleSubractClick() {
        console.log("subtracting")
    }
    handleTotalClick() {
        console.log("totaling")
    }
    handleOneClick() {
        this.setState(prevState => {
            return {formSpace: prevState}
        })
    }
    render() {
        return (
            <div className="calculator-container">
                <div className="add">
                    <h1>{this.state.add}</h1>
                    <button onClick={this.handleAddClick}>Plus</button>
                </div>
                <div className="subtract">
                    <h1>{this.state.subtract}</h1>
                    <button onClick={this.handleSubractClick}>Subtract</button>
                </div>
                <div className="total">
                    <h1>{this.state.total}</h1>
                    <button onClick={this.handleTotalClick}>Enter</button>
                </div>
            <div className="numbers">
                <div className="form">
                    <h1>{this.state.formSpace}</h1>
                </div>
                <div className="one">
                    <button onClick={this.handleOneClick}>1</button>
                </div>
            </div>
            </div>


        )
    }
}
export default Calculator
