import React from 'react'

class Calculator extends React.Component {
    constructor(){
        super()
        this.state = {
            input: ""
        }
        this.handleClick = this.handleClick.bind(this)
    }
    handleClick() {
        this.setState(prevState => {
            console.log("clicked")
            return {input: prevState.input}
        })
    }
    render() {
        return (
            <div className="row m-3">
                <div>
                    <h1>{this.state.input}</h1>
                </div>
                <div className="col-sm">
                    <button onClick={this.handleClick} className="btn btn-dark m-1 btn-lg">1</button>
                    <button onClick={this.handleClick} className="btn btn-dark m-1 btn-lg">2</button>
                    <button onClick={this.handleClick} className="btn btn-dark m-1 btn-lg">3</button>
                    <br />
                    <button onClick={this.handleClick} className="btn btn-dark m-1 btn-lg">4</button>
                    <button onClick={this.handleClick} className="btn btn-dark m-1 btn-lg">5</button>
                    <button onClick={this.handleClick} className="btn btn-dark m-1 btn-lg">6</button>
                    <br />
                    <button onClick={this.handleClick} className="btn btn-dark m-1 btn-lg">7</button>
                    <button onClick={this.handleClick} className="btn btn-dark m-1 btn-lg">8</button>
                    <button onClick={this.handleClick} className="btn btn-dark m-1 btn-lg">9</button>
                    <br />
                    <button onClick={this.handleClick} className="btn btn-dark m-1 btn-lg">0</button>
                </div>
            </div>
        )
    }
}
export default Calculator
