import React from 'react'
import Numbers from '../Numbers'

class Operations extends React.Component {
    constructor(){
        super()
        this.state = {
            addition: ""
        }
        this.handleAdd = this.handleAdd.bind(this)
        this.handleSubract = this.handleSubract.bind(this)
        this.handleMultiply = this.handleMultiply.bind(this)
        this.handleDivide = this.handleDivide.bind(this)
        this.handleEqual = this.handleEqual.bind(this)
    }
    handleAdd(event) {
        console.log(addition)
        this.setState({addition: event.target.value})
    }
    handleSubract() {
        console.log('SUbtract')
    }
    handleMultiply() {
        console.log('Multi')
    }
    handleDivide() {
        console.log('Divide')
    }
    handleEqual() {
        console.log('Equal')
    }
    render() {
        return(
            <div className="col-sm">
                <button onClick={this.handleAdd}>+</button>
                <button onClick={this.handleSubract}>&minus;</button>
                <button onClick={this.handleMultiply}>&times;</button>
                <button onClick={this.handleDivide}>&divide;</button>
                <button onClick={this.handleEqual}>=</button>

            </div>
        )
    }
}

export default Operations
